import { Configuration } from "webpack";
const FileManagerPlugin = require('filemanager-webpack-plugin');

export const onEndConfigurations: Configuration = {
    plugins: [
        new FileManagerPlugin({
            events: {
                onEnd: [
                    {
                        move: [
                            { source: './dist/src', destination: './dist/types' }
                        ],
                        delete: [
                            './dist/webpack*',
                            './dist/test*',
                            './dist/jest*',
                            './dist/config*',
                            './dist/main.js*',
                        ]
                    }
                ],
            }
        })
    ]
}