/**
 * Take a string (url) and extract the keyword that is surrounded with curly braces
 * considering it as the ID for the document
 * 
 * @param {string} url 
 */
export function extractIdPropertyFromURL(url?: string) {

    let idPropertyWithBraces = '';
    let idPropertyName = '';

    if (url) {
        idPropertyWithBraces = /\{\w+\}/.exec(url ?? '')?.toString() ?? '';
        // remove curly braces
        idPropertyName = idPropertyWithBraces.replace(/[\{-\}]/g, '');

        return [idPropertyWithBraces, idPropertyName];
    }


    return ['', ''];
}
