import * as webpackMerge from 'webpack-merge';
import { BaseConfiguration } from './config/base.config'
import { NodeConfiguration } from './config/node.config';
import { BrowserConfiguration } from './config/browser.config';
import { onStartConfigurations } from './config/on-start.config';
import { onEndConfigurations } from './config/on-end.config';


let PlatformsConfigurations = [
    onStartConfigurations,
    NodeConfiguration,
    BrowserConfiguration,
    onEndConfigurations,
];


export default PlatformsConfigurations.map(config => webpackMerge.merge(config, BaseConfiguration));