import { BaseURLOptions } from "./base.options";
export interface SearchURLOptions extends BaseURLOptions {
    query: string;
    skip?: number;
    limit?: number;
    filter?: string;
    /**
     * user choose how the link of one item looks like in his/her website
     *
     * example:
     * www.example.com/news/{id}
     * or
     * www.example.com/news/{title}
     *
     * then id or title will be replace with the real property(id or title)
     * from the item response to correctly form the feedback url
     */
    itemURLSchema?: string;
}
export interface SearchFeedbackURLOptions extends Omit<SearchURLOptions, "skip" | "limit" | "filter" | "itemURLSchema"> {
    url?: string;
    itemId?: number;
    itemOrder?: number;
}
