/**
 * take an object of key values, it keeps only truthy values depending on
 * the predicate function passed
 * 
 * @param {object} obj 
 * @param {function} func 
 * 
 * @returns {object}
 */
export function pickBy(obj: any, func: (v: any) => boolean) {

    Object.keys(obj).forEach(key => {
        if (!func(obj[key])) {
            delete obj[key]
        }
    });

    return obj;
}