import { BrowserHttpClient } from "./browser";
import { NodeHttpClient } from "./node";


/**
 * NOTE:
 * 
 * process.env.BUILD_TARGET is env variable passed by webpack at build time
 * when it equals `BROWSER` that means we are building the sdk to be used in browser so we can use `fetch` internally
 * otherwise we use nodeHttpClient which use `axios` internally
 */
export const HttpClient = process.env.BUILD_TARGET == 'BROWSER' ? BrowserHttpClient : NodeHttpClient;
